<?phpclass Behave2 extends Controller{    private $ivr_message;        function __construct() {        $this->ivr_message=(object) parse_ini_file('application/ivr_message.ini');    }        function index(){        $params=$this->urlPara();        $this->loadModel('orders_model');        $orders_model=new Orders_model();        $this->loadModel('system_model');        $system_model=new System_model();        $this->loadModel('transactions');        $transactions=new Transactions();                $bahavoir=$system_model->get_bahavior();        $data['amount']=$bahavoir['amount'];        $order['amount']=$bahavoir['amount'];        $order['phone']=  user_in_filter($_REQUEST['From']);        $order_id=$orders_model->create_phone_order($order);        $txnid=$transactions->initiate($order_id);        $data['redirect']=  Config::$base_url."/twiml/confirm_payment/".$txnid.".php";        $data['ivr_amount']=  $this->ivr_message->amount;        $data['amount_confirmation']= $this->ivr_message->amount_confirmation;        $this->loadView('twiml_amount_confirm',$data);            }            //end of class    }